import React from 'react'
import {
    useParams,
} from "react-router-dom";
import houselisting from '../assets/mocks/data';


export default function HouseDetails() {

    let houseCardId = useParams();
    // let num = props.match.params.id;

    const house = houselisting.find(house => house.sys.id === houseCardId.id);

    if (house === undefined) {
        console.log("undefined");
    }

    const houseInfo = house.fields;
    const { title, description, capacity, type, images, price, city, state, zip, size
    } = houseInfo;

    return (
        <div>
            <section className= "house-details-container">

                <div className="house-details-images-container">{images.map((image, index) =>
                    <img key={index} src={image.fields.file.url} alt={title} />)}</div>

                <div className="house-details-content-container">

                        <article className="description">
                            <h3>Details</h3>
                            <p>{description}</p>
                        </article>
                        <article className="information">
                            <h3>Info</h3>
                            <p>Type: {type} </p>
                            <p>price : ${price}</p>
                            <p>size : {size} SQFT</p>
                            <p>
                                max capacity :
                {capacity > 1 ? `${capacity} people` : `${capacity} person`}
                            </p>
                            <p>{`${city} • ${state} • ${zip}`}</p>
                        </article>

                </div>
            </section>


        </div>
    )
}
