import React from 'react'
import {
    useParams,
} from "react-router-dom";
import houselisting from '../assets/mocks/data';
import sf_housing_data from '../assets/mocks/sf_housing_data.json'
import defaultImg from '../assets/images/room-1.jpeg'


export default function HouseDetails() {

    let houseCardId = useParams();
    // let num = props.match.params.id;
    console.log(houseCardId);

     const houses = sf_housing_data.find(house => 
        (house.zpid === houseCardId || house.buildingId === houseCardId.id)
        );

    if (houses === undefined) {
        console.log("Undefined");
    } else {
        console.log(houses);
    }

    // const houseInfo = house.fields;
    // const { title, description, capacity, type, images, price, city, state, zip, size
    // } = houseInfo;

    let price = houses.price;
    let imgSrc = houses.imgSrc;
    let id, title, statusType, beds, baths, city, state, zip, location;

    if (!!houses.zpid) {
        // with zpid
        id = houses.zpid;
        title = houses.hdpData.homeInfo.streetAddress;
        statusType = houses.statusText;
        beds = houses.beds;
        baths = houses.baths;
        city = houses.hdpData.homeInfo.city;
        state = houses.hdpData.homeInfo.state;
        zip = houses.hdpData.homeInfo.zipcode;
        location = city + " • " + state + " • " + zip;
    } else {
        // with buildingId
        id = houses.buildingId;
        title = houses.statusText;
        statusType = houses.statusType;
        beds = houses.minBeds;
        baths = houses.minBaths;
        location = " ";
    }

    return (
        <div>
            <section className= "house-details-container">

                {/* <div className="house-details-images-container">{images.map((image, index) =>
                    <img key={index} src={image.fields.file.url} alt={title} />)}</div> */}
                <div className="house-details-images-container">
                    <img src={imgSrc} alt="house listing"></img>
                    </div>

                <div className="house-details-content-container">

                        <article className="description">
                            <h3>Details</h3>
                            <p>description</p>
                        </article>
                        <article className="information">
                            <h3>Info</h3>
                            <p>Type: type</p>
                            <p>price : $price</p>
                            <p>sze : size SQFT</p>
                            <p>{location}</p>
                            {/* <p>
                                max capacity :
                {capacity > 1 ? `${capacity} people` : `${capacity} person`}
                            </p>
                            <p>{`${city} • ${state} • ${zip}`}</p> */}

                        </article>

                </div>
            </section>


        </div>
    )
}
